package com.project.model;

import jakarta.persistence.Entity;
import jakarta.persistence.GeneratedValue;
import jakarta.persistence.GenerationType;
import jakarta.persistence.Id;
import jakarta.persistence.Enumerated;
import jakarta.persistence.EnumType;

/**
 * Entity representing a Claim in the insurance system. Maps to a database table
 * storing claim details.
 */
@Entity
public class Claim {

	/**
	 * Unique identifier for the claim. Auto-generated by the database.
	 */
	@Id
	@GeneratedValue(strategy = GenerationType.IDENTITY)
	private Long claimId;

	/**
	 * Identifier of the associated insurance policy.
	 */
	private Long policyId;

	/**
	 * Identifier of the customer who filed the claim.
	 */
	private Long customerId;

	/**
	 * Amount being claimed by the customer.
	 */
	private Double claimAmount;

	/**
	 * Current status of the claim. Stored as a string in the database.
	 */
	@Enumerated(EnumType.STRING)
	private Status status;

	/**
	 * Enum representing the possible states of a claim.
	 */
	public enum Status {
		FILED, // Claim has been filed
		UNDER_REVIEW, // Claim is being reviewed
		APPROVED, // Claim has been approved
		REJECTED // Claim has been rejected
	}

	// -------------------- Getters and Setters --------------------

	/**
	 * Gets the claim ID.
	 * 
	 * @return claimId
	 */
	public Long getClaimId() {
		return claimId;
	}

	/**
	 * Sets the claim ID.
	 * 
	 * @param claimId the unique identifier for the claim
	 */
	public void setClaimId(Long claimId) {
		this.claimId = claimId;
	}

	/**
	 * Gets the policy ID associated with the claim.
	 * 
	 * @return policyId
	 */
	public Long getPolicyId() {
		return policyId;
	}

	/**
	 * Sets the policy ID.
	 * 
	 * @param policyId the identifier of the insurance policy
	 */
	public void setPolicyId(Long policyId) {
		this.policyId = policyId;
	}

	/**
	 * Gets the customer ID who filed the claim.
	 * 
	 * @return customerId
	 */
	public Long getCustomerId() {
		return customerId;
	}

	/**
	 * Sets the customer ID.
	 * 
	 * @param customerId the identifier of the customer
	 */
	public void setCustomerId(Long customerId) {
		this.customerId = customerId;
	}

	/**
	 * Gets the claim amount.
	 * 
	 * @return claimAmount
	 */
	public Double getClaimAmount() {
		return claimAmount;
	}

	/**
	 * Sets the claim amount.
	 * 
	 * @param claimAmount the amount being claimed
	 */
	public void setClaimAmount(Double claimAmount) {
		this.claimAmount = claimAmount;
	}

	/**
	 * Gets the current status of the claim.
	 * 
	 * @return status
	 */
	public Status getStatus() {
		return status;
	}

	/**
	 * Sets the status of the claim.
	 * 
	 * @param status the current state of the claim
	 */
	public void setStatus(Status status) {
		this.status = status;
	}

	/**
	 * @param claimId
	 * @param policyId
	 * @param customerId
	 * @param claimAmount
	 * @param status
	 */
	public Claim(Long claimId, Long policyId, Long customerId, Double claimAmount, Status status) {
		super();
		this.claimId = claimId;
		this.policyId = policyId;
		this.customerId = customerId;
		this.claimAmount = claimAmount;
		this.status = status;
	}

	/**
	 * 
	 */
	public Claim() {
		super();
	}
	
	
	
}
